import { useState } from 'react'

const PersonForm = ({addName,newName,handleNameChange,handleNumberChange}) => {
  <form onSubmit={addName}>
  <div>
    name: <input value={newName} onChange={handleNameChange}/>
  </div>
  <div>
    number: <input value={newNumber} onChange={handleNumberChange}/>
    </div>
  <div>
    <button type="submit">add</button>
  </div>
</form>
}

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas', number: '040-123456', id: 1 },
    { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
    { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
    { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newSearch, setNewSearch] = useState('')

  const addName = (event) => {
    event.preventDefault()
    const personObject = {
      name: newName,
      number: newNumber
    }

    const checkDuplicate = persons.some(element => {
      if (element.name === personObject.name){
        return true;
      }

      return false;
    })

    if (checkDuplicate){
      alert(`${newName} is already added to phonebook`)
      setNewName('')
      setNewNumber('')
    } else {
      setPersons(persons.concat(personObject))
      setNewName('')
      setNewNumber('')
    }

  }

  const handleNameChange = (event) => {
      setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    setNewNumber(event.target.value)
  }

  const handleSearchChange = (event) =>{
    setNewSearch(event.target.value)
  }

  const DisplayNumbers = () => {
    if (newSearch === ''){
      return(persons.map(person => <p>{person.name} {person.number}</p>)) 
    } else {

      const filteredList = persons.filter(person => person.name.includes(newSearch))
      return (
        filteredList.map(person => <p>{person.name} {person.number}</p>)
      )

    }


  }








  return (
    <div>
      <h2>Phonebook</h2>

      <div>
        filter shown with <input value={newSearch} onChange={handleSearchChange}/>
      </div>

      <PersonForm addName={addName} newName={newName} handleNameChange={handleNameChange} handleNumberChange={handleNumberChange}/>

      <h2>Numbers</h2>
      <DisplayNumbers />

    </div>
  )
}

export default App
